import React, {Component} from 'react';
import { Timeline } from 'antd';
import axios from 'axios';
import Header from './Components/Header';
import TimelineCard from "./Components/TimelineCard";


class App extends Component {
  state = {
    purchase: null
  }
  componentWillMount() {
    axios.get('https://storage.googleapis.com/dito-questions/events.json')
        .then(res => {
          let events = res.data.events;
          let newPurchase = [];
          let currentPurchase = 0;

          events.forEach(event => {
            if (event.event === 'comprou') {
              let newEvent = {
                  ...event,
                  products: []
              }
              newPurchase.push(newEvent);
              console.log(event)
            }

            else if (event.event === 'comprou-produto') {
              newPurchase[0].products.push(event)
              currentPurchase++
            }
          })
          console.log(newPurchase)
          this.setState({purchase: newPurchase})
        })
        .catch(error => console.log(error))
  }
  render(){
    return (
        <div>
          <Header />
          <Timeline style={{margin: '15px', marginRight: '8px'}}>
            {
              (this.state.purchase) &&
                  this.state.purchase.map((event,i) => {
                    return <TimelineCard key={i} />
                  })
            }
          </Timeline>
        </div>
    );
  }
}


export default App;
