import React, {Component} from 'react';
import { Timeline } from 'antd';
import axios from 'axios';
import Header from './Components/Header';
import TimelineCard from "./Components/TimelineCard";


class App extends Component {
  state = {
    purchase: null
  }
  componentWillMount() {
    axios.get('https://storage.googleapis.com/dito-questions/events.json')
        .then(res => {
          let events = res.data.events;
          let newPurchase = [];
          let currentPurchase = 0;

          events.forEach(event => {
            if (newPurchase[currentPurchase]) {
              if (event.event === 'comprou-produto') {
                newPurchase[currentPurchase].produtos.push(event)
              }
              else if (event.event === 'comprou') {
                newPurchase[currentPurchase].compra = event
              }
            }
            else {
              newPurchase[currentPurchase] = {compra:null, produtos:[]}
              if (event.event === 'comprou-produto') {
                newPurchase[currentPurchase].produtos.push(event)
              }

            }

          })
          console.log(newPurchase)
          this.setState({purchase: newPurchase})
        })
        .catch(error => console.log(error))
  }
  render(){
    return (
        <div>
          <Header />
          <Timeline style={{margin: '15px', marginRight: '8px'}}>
            {
              (this.state.purchase) &&
                  this.state.purchase.map((event,i) => {
                    return <TimelineCard key={i} />
                  })
            }
          </Timeline>
        </div>
    );
  }
}


export default App;
