import React, {Component} from 'react';
import axios from 'axios';
import Header from './Components/Header';
import Timeline from './Components/Timeline';

class App extends Component {
  state = {
    purchase: null,
    shopping: null,
    product: null
  }
  componentWillMount() {
    axios.get('https://storage.googleapis.com/dito-questions/events.json')
        .then(res => {
          let ShoppingPurchase = [];
          let ProductPurchase = [];
          let newPurchase = [];
          res.data.events.forEach(event => {
            if (event.event === 'comprou-produto') ProductPurchase.push(event);
            else if (event.event === 'comprou') ShoppingPurchase.push(event);
          });
          ShoppingPurchase.forEach(shopping => {
            let date = shopping.timestamp.slice(0,10);
            ProductPurchase.forEach(product => {
              if (product.timestamp.slice(0,10) === date) console.log('YAAAAY')
            })
          })
          this.setState({shopping: ShoppingPurchase, product: ProductPurchase})
        })
        .catch(error => console.log(error))
  }
  render(){
    return (
        <div>
          <Header />
          <Timeline purchase={this.state.purchase}/>
        </div>
    );
  }
}


export default App;
