import React, {Component} from 'react';
import axios from 'axios';
import Header from './Components/Header';
import Timeline from './Components/Timeline';


class App extends Component {
  state = {
    purchase: null
  }
  componentWillMount() {
    axios.get('https://storage.googleapis.com/dito-questions/events.json')
        .then(res => {
          let events = res.data.events;
          let newPurchase = [];
          let currentPurchase = 0;

          events.forEach(event => {
            if (!newPurchase[currentPurchase]) newPurchase[currentPurchase] = {compra:null, produtos:[]}
            if (event.event === 'comprou-produto') newPurchase[currentPurchase].produtos.push(event)
            else if (event.event === 'comprou') {
              newPurchase[currentPurchase].compra = event;
              currentPurchase++;
            }
          });
          this.setState({purchase: newPurchase})
        })
        .catch(error => console.log(error))
  }
  render(){
    return (
        <div>
          <Header />
          <Timeline purchase={this.state.purchase}/>
        </div>
    );
  }
}


export default App;
