import React from 'react';
import {Card, Timeline, Table} from "antd";
import Moment from 'react-moment';

const columns = [{title: 'Produto', dataIndex: 'produto'}, {title: 'Preço', dataIndex: 'preco'}];
const TimelineCard = (props) => {
  const data = [];
  props.purchase.produtos.forEach(product => {
    let newData = {key: null, produto: null, preco: null}
    product.custom_data.forEach(custom => {
      if (custom.key === 'product_name') newData.produto = custom.value
      else if (custom.key === 'transaction_id') newData.key = custom.value
      else if (custom.key === 'product_price') newData.preco = custom.value
    })
    data.push(newData)
  })

  return (
      <Timeline.Item>
        <Card size="small" style={{ width: '70%' }}>
            <p style={{display: 'inline', marginRight: '8px'}}>
              <i className="fas fa-calendar-week" style={{marginRight: '3px'}}/><Moment format="DD/MM/YYYY">{props.purchase.compra.timestamp}</Moment>
            </p>
            <p style={{display: 'inline', marginRight: '8px'}}>
              <i className="far fa-clock" style={{marginRight: '3px'}}/><Moment format="HH:mm">{props.purchase.compra.timestamp}</Moment>
            </p>
            <p style={{display: 'inline', marginRight: '8px'}}>
              <i className="fas fa-map-marker-alt" style={{marginRight: '3px'}}/>
              {
                props.purchase.compra.custom_data.map(data => { if (data.key === 'store_name') return data.value })
              }
            </p>
            <p style={{display: 'inline', marginRight: '8px'}}>
              <i className="fas fa-dollar-sign" style={{marginRight: '3px'}}/>{props.purchase.compra.revenue.toFixed(2)}
            </p>
          <Table columns={columns} dataSource={data} size="small" pagination={false} style={{marginTop: '10px'}}/>
        </Card>
      </Timeline.Item>
  );
};

export default TimelineCard;
